import React from "react";

import "./ChatRoom.css";
import useChat from "../useChat";

const ChatRoom = (props) => {
  const { roomId } = props.match.params;
  const { messages, sendMessage } = useChat(roomId);
  const [newMessage, setNewMessage] = React.useState("");
  let keysPressed = {'w':false, 'a':false, 's':false, 'd':false};

  document.addEventListener('keydown', (event) => {
    if(keysPressed[event.key] === false){
      keysPressed[event.key] = true;
    }
  });

  document.addEventListener('keyup', (event) => {
    if(keysPressed[event.key]){
      keysPressed[event.key] = false;
    }
  });

  const handleNewMessageChange = (event) => {
    setNewMessage(event.target.value);
  };

  const handleKeyPress = (event) => {
    sendMessage(JSON.stringify(keysPressed));
    setNewMessage("");
    // keysPressed[event.key] = true;
    // sendMessage(JSON.stringify(keysPressed));
    // setNewMessage("");
    // keysPressed[event.key] = true;
    // if (keysPressed['b'] && event.key == 'a') {
    //   sendMessage("Hello");
    //   setNewMessage("");
    // if(event.key === 'w' || event.key === 'a' || event.key === 's' || event.key === 'd'){
    //   sendMessage(event.key+event.key+event.key+event.key);
    //   setNewMessage("");
    // }
  }

  return (
    <div className="chat-room-container">
      <h1 className="room-name">Room: {roomId}</h1>
      <div className="messages-container">
        <ol className="messages-list">
          {messages.map((message, i) => (
            <li
              key={i}
              className={`message-item ${
                message.ownedByCurrentUser ? "my-message" : "received-message"
              }`}
            >
              {message.body}
            </li>
          ))}
        </ol>
      </div>
      <textarea
        value={newMessage}
        onChange={handleNewMessageChange}
        placeholder="Write message..."
        className="new-message-input-field"
      />
      <button onKeyPress={handleKeyPress} className="send-message-button">
        Send
      </button>
    </div>
  );
};

export default ChatRoom;
